float barChartTotalValue(struct bed *bed)
/* Return total of all category values */
{
int i;
float sum = 0.0;
for (i=0; i<bed->expCount; i++)
    sum += bed->expScores[i];
return sum;
}

float barChartMaxValue(struct bed *bed, int *categIdRet)
/* Return value and id of category with highest value for this item */
{
int i;
float maxScore = 0.0;
assert(categIdRet);
for (i=0; i<bed->expCount; i++)
    {
    float score = bed->expScores[i];
    if (score > maxScore)
        {
        maxScore = score;
        *categIdRet = i;
        }
    }
return maxScore;
}

