#correspondence of UCSC chromosome names to refseq, genbank, and ensembl names
CREATE TABLE chromXref (
    ucsc varchar(255) not null,	# UCSC genome browser chromosome name
    refseq varchar(255) not null,	# RefSeq assembly accession (n/a when not available)
    genbank varchar(255) not null,	# Genbank assembly accession (n/a when not available)
    ensembl varchar(255) not null,	# Ensembl chromosome name (n/a when not available)
              #Indices
    PRIMARY KEY(ucsc),
    KEY(refseq),
    KEY(genbank),
    KEY(ensembl)
);
