#Information from dbSNP at the reference SNP level
CREATE TABLE dbSnpRS (
    rsId int unsigned not null,	# rs identifier
    avHet float not null,	# the average heterozygosity from all observations
    avHetSE float not null,	# the Standard Error for the average heterozygosity from all observations
    valid varchar(255) not null,	# the validation status of the SNP
    base1 char(1) not null,	# the base of the first allele
    base2 char(1) not null,	# the base of the second allele
    assembly varchar(255) not null,	# the sequence in the ucsc assembly
    alternate varchar(255) not null,	# the sequence of the alternate allele
              #Indices
    PRIMARY KEY(rsId)
);
