# automatic way.

#All genes in Genbank genome entry
CREATE TABLE gbRNAs (
    bin int unsigned not null,
    chrom varchar(255) not null,	# chromosome or plasmid
    chromStart int unsigned not null,	# Start position in chromosome
    chromEnd int unsigned not null,	# End position in chromosome
    name varchar(255) not null,	        # Name of gene
    score int unsigned not null,	# Score from 900-1000.  1000 is best
    strand char(1) not null,	        # Value should be + or -
    descript varchar(255) not null,	# Genbank description of gene
              #Indices
    PRIMARY KEY(name)
);
